document.addEventListener("DOMContentLoaded",async function(){let n=await(await fetch(window.API_BASE_URL+"/recipes")).json(),a=document.getElementById("recipe-container");Object.keys(n).forEach(e=>{n[e].forEach(e=>{var n=document.createElement("div");n.classList.add("recipe-card"),n.innerHTML=`
                <img src="${e.image}" alt="${e.name}">
                <h3>${e.name}</h3>
                <p>${e.description}</p>
                <a href="recipe-details.html?recipename=${encodeURIComponent(e.name)}">View Recipe</a>
            `,a.appendChild(n)})})});