document.getElementById("recipe-form").addEventListener("submit",function(e){e.preventDefault();e={recipeName:document.getElementById("recipe-name").value,ingredients:document.getElementById("ingredients").value.split("\n"),instructions:document.getElementById("instructions").value.split("\n"),category:document.getElementById("category").value,prepTime:document.getElementById("prep-time").value,cookTime:document.getElementById("cook-time").value,servingSize:document.getElementById("serving-size").value};fetch(window.API_BASE_URL+"/submit-recipe",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)}).then(e=>e.json()).then(e=>{document.getElementById("response-message").textContent=e.message,document.getElementById("response-message").style.color="green",document.getElementById("recipe-form").reset()}).catch(e=>{console.error("Error:",e),document.getElementById("response-message").textContent="Error submitting recipe.",document.getElementById("response-message").style.color="red"})});